using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;

namespace Midoliy.Office.Interop
{
    public static class IntegerEx
    {
        static IntegerEx()
        {
        }

        public static string ToColumnName(this int @this)
        {
            if (@this <= 0)
                throw new ArgumentOutOfRangeException("Should be a value greater than 0.");

            if (@this < 27)
                return _1digits.Substring(@this - 1, 1);

            if (@this < (27 + 26 * 26))
                return _2digits.Substring((@this - 27) * 2, 2);

            var acc = new Stack<char>();
            while (0 < @this)
            {
                @this -= 1;
                var padding = @this % 26;
                acc.Push((char)('A' + padding));
                @this /= 26;
            }

            return string.Concat(acc);
        }

        private const string _1digits = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";
        private const string _2digits = "";
    }
}
